(221, 231, 'ZKTeco iClock9000-G (GPRS/3G) Time Attendance Terminal Machine', '1.0', '<p>Specification\r\n</p><p>Main Features\r\n</p><p>Fingerprint Capacity\r\n</p><p>6,000\r\n</p><p>ID Card Capacity\r\n</p><p>6,000\r\n</p><p>Record Capacity\r\n</p><p>200,000\r\n</p><p>Display\r\n</p><p>2.8-inch TFT Screen\r\n</p><p>Communication\r\n</p><p>TCP/IP, USB-Host, GPRS, Wi-Fi (Optional) , 3G (Optional)\r\n</p><p>Standard Functions\r\n</p><p>SMS, DTS, Scheduled-bell, Self-Service Query, Automatic Status Switch, T9 Input, Photo ID, Multiverification, 12V Output, RS232 Printer (Optional Cable), ADMS, 2,600 mAh Backup Battery.\r\n</p><p>Interface\r\n</p><p>Access Control 3rd Party Electric Lock, Exit Button, Alarm\r\n</p><p>Others\r\n</p><p>Verification Speed: 1 Sec.\r\n</p><p>Operating Temp: 0°c – 45°c\r\n</p><p>Operating Humidity: 20% – 80%\r\n</p><p>Physical Specification\r\n</p><p>Dimension\r\n</p><p>167.5×148.8×32.2 mm (Length x Width x Thickness)\r\n</p><p>Weight\r\n</p><p>380g\r\n</p><p>Power Supply\r\n</p><p>DC 12V 1.5A\r\n</p><p>Others\r\n</p><p>High veriﬁcation speed\r\n</p><p>Professional ﬁrmware and platform make it more ﬂexible\r\n</p><p>Intuitive and stunning UI design\r\n</p><p>Manufacture Warranty\r\n</p><p>Warranty\r\n</p><p>01 year warranty</p>', '<p>Key Features\r\n</p><p>Model: ZKTeco iClock9000-G\r\n</p><p>Fingerprint Capacity: 6,000\r\n</p><p>ID Card Capacity: 6,000\r\n</p><p>High veriﬁcation speed\r\n</p><p>Display 2.8-inch TFT Screen</p>', 'en', '2021-11-27 17:02:07', '2021-11-27 17:02:07'),
(222, 232, 'ZKTeco K40 Time & Attendance Terminal Machinedance Terminal Machine', '1.0', '<p>Specification\r\n</p><p>Fingerprint Capacity 1000\r\n</p><p>ID Card Capacity 1000 (Optional)\r\n</p><p>Record Capacity 100,000\r\n</p><p>Display 2.8-Inch TFT Screen\r\n</p><p>Communication TCP/IP, USB Host\r\n</p><p>Standard Function Work Code, SMS, DST, Self-Service Query,\r\n</p><p>Automatic Status Switch, Scheduled-Bell, T9 Input, 9 Digit User ID, Built-In Battery,\r\n</p><p>Simple Access Control Or External Bell\r\n</p><p>Optional Function ID/Mifare\r\n</p><p>Software ZKTime5.0\r\n</p><p>Power Supply DC 12V 1.5A\r\n</p><p>Verification Speed &lt;0.5 Sec.\r\n</p><p>Operating Temp 0°C-45°C\r\n</p><p>Operating Humidity 20%-80%\r\n</p><p>Dimension 184*136*37.6(Length*Width*Thickness)</p>', '<p>Key Features\r\n</p><p>Model: ZKTeco K40\r\n</p><p>Fingerprint Capacity 1000\r\n</p><p>ID Card Capacity 1000 (Optional)\r\n</p><p>Record Capacity 100,000\r\n</p><p>Display 2.8-Inch TFT Screen</p>', 'en', '2021-11-27 17:06:25', '2021-11-27 17:06:25'),
